package com.ncpl.assignmentweek1;

public class Printingdefaultvalues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int defaultIntValue=0;
		double defaultDoubleValue=0.0;
		float defaultFloatValue=0.0f;
		long defaultLongValue=0L;
		boolean defaultBooleanValue = false;
		char defaultCharValue = '\u0000';
		short defaultShortValue = 0;
		
		System.out.println("Default value of integer : "+defaultIntValue);
		System.out.println("Default value of double : "+defaultDoubleValue);
		System.out.println("Default value of float : "+defaultFloatValue);
		System.out.println("Default value of long : "+defaultLongValue);
		System.out.println("Default value of boolean : "+defaultBooleanValue);
		System.out.println("Default value of char :  "+defaultCharValue);
		System.out.println("Default value of short : "+defaultShortValue);
		
		//int range capacity is -2^31 to 2^31-1 or -2*10^9 to 2*10^9
		//long range capacity is -2^63 to 2^63-1 or -8*10^-18 to 8*10^18
		//primitive data types in java - int,long,short,double,float,boolean,char
		//wrapper classes in java - Integer,Long,Double,Boolean,Character,Float,Short..................
		//In java we see everything in an object form and hence data types are not objects. Wrapper classes are used to represent primitive data types as objects.
		//string is also a data type but it is a class type but not a primitive data type.


	}

}
