package com.ncpl.assignmentweek1;

public class WrapperClassDefaultValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Integer defaultIntegerValue = 0;
		Short defaultShortValue = 0;
		Long defaultLongValue = 0L;
		Double defaultDoubleValue = 0.0;
		Float defaultFloatValue = 0.0f;
		Character defaultCharacterValue = '\u0000';
		Boolean defaultBooleanValue = false;
		
		System.out.println("Default value of integer : "+defaultIntegerValue);
		System.out.println("Default value of double : "+defaultDoubleValue);
		System.out.println("Default value of float : "+defaultFloatValue);
		System.out.println("Default value of long : "+defaultLongValue);
		System.out.println("Default value of boolean : "+defaultBooleanValue);
		System.out.println("Default value of char :  "+defaultCharacterValue);
		System.out.println("Default value of short : "+defaultShortValue);
		
		
		
		

	}

}
